-- 테이블 shuim의 구조를 덤프합니다. member
CREATE TABLE IF NOT EXISTS `member` (
  `seq` int(11) NOT NULL AUTO_INCREMENT COMMENT '고유번호',
  `email` varchar(60) DEFAULT NULL COMMENT '이메일(ID)',
  `passwd` varchar(60) DEFAULT NULL COMMENT '비밀번호',
  `name` varchar(20) DEFAULT '' COMMENT '사용자명',
  `mobile` varchar(13) DEFAULT '' COMMENT '휴대폰',
  `birthday` varchar(8) DEFAULT '' COMMENT '생년월일',
  `gender` varchar(1) DEFAULT '' COMMENT '성별',
  `nick` varchar(10) DEFAULT '' COMMENT '닉네임',
  `last_login` varchar(20) DEFAULT '' COMMENT '최종 로그아웃',
  `login_count` int(11) DEFAULT '0' COMMENT '로그인 카운트',
  `reg_date` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '가입일',
  `state` int(1) DEFAULT '1' COMMENT '계정상태',
  `drop_date` varchar(20) DEFAULT '' COMMENT '탈퇴일',
  `drop_reason` varchar(200) DEFAULT '' COMMENT '탈퇴 이유',
  PRIMARY KEY (`seq`)
) ENGINE=InnoDB AUTO_INCREMENT=92 DEFAULT CHARSET=utf8;

